mapper.select:
SELECT

FROM sys_dict a

WHERE a.id = #{id}

SELECT
FROM sys_dict a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND type = #{type}
AND label = #{label}
AND description LIKE '%'||#{description}||'%' '%'+#{description}+'%' CONCAT('%', #{description}, '%')
AND CONCAT() LIKE '%'||#{globalSearch}||'%' '%'+#{globalSearch}+'%' CONCAT('%', #{globalSearch}, '%')
ORDER BY type, sort, update_date DESC

mapper.update:
UPDATE sys_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
